import React, { useState, useEffect } from "react";
import axios from "axios";
import "../Styles/Navbar.css";

function Navbar() {
    const [showModal, setShowModal] = useState(false);
    const [isSignIn, setIsSignIn] = useState(true);
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    

    useEffect(() => {
        checkLoggedInStatus();
    }, []);



    const handleToggleModal = () => {
        setShowModal(!showModal);
    };

    const handleToggleForm = () => {
        setIsSignIn(!isSignIn);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = isSignIn
                ? await axios.post("/users/signin", { username, password })
                : await axios.post("/users/signup", { username, password });

            setLoggedInUser(response.data.username);
            setShowModal(false);
            setUsername("");
            setPassword("");
        } catch (error) {
            console.error("Authentication failed:", error.response.data);
        }
    };

    const handleLogout = async () => {
        try {
            await axios.get("/users/logout");
            setLoggedInUser(null);
        } catch (error) {
            console.error("Logout failed:", error);
        }
    };

    return (
        <div className="navbar">
            <h1>My Diary</h1>
            {loggedInUser ? (
                <div>
                    <span>Welcome, {loggedInUser}</span>
                    <button className="logout-btn" onClick={handleLogout}>Logout</button>
                </div>
            ) : (
                <button className="sign-in-btn" onClick={handleToggleModal}>
                    Sign In
                </button>
            )}
            {showModal && (
                <div className="modal-wrapper">
                    <div className="modal" onClick={handleToggleModal}>
                        <div className="modal-content" onClick={(e) => e.stopPropagation()}>
                            <span className="close" onClick={handleToggleModal}>&times;</span>
                            <h2>{isSignIn ? "Sign In" : "Sign Up"}</h2>
                            <form className="auth-form" onSubmit={handleSubmit}>
                                <input type="text" placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)} />
                                <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
                                {!isSignIn && <input type="password" placeholder="Confirm Password" />}
                                <button type="submit">{isSignIn ? "Sign In" : "Sign Up"}</button>
                            </form>
                            <p onClick={handleToggleForm}>
                                {isSignIn ? "New to My Diary? Sign Up" : "Already have an account? Sign In"}
                            </p>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
}

export default Navbar;
