import React, { useState } from "react";
import "../Styles/Navbar.css";

function Navbar() {
    const [showModal, setShowModal] = useState(false);
    const [isSignIn, setIsSignIn] = useState(true);

    const handleToggleModal = () => {
        setShowModal(!showModal);
    };

    const handleToggleForm = () => {
        setIsSignIn(!isSignIn);
    };

    return (
        <div className="navbar">
            <h1>My Journal</h1>
            <button className="sign-in-btn" onClick={handleToggleModal}>
                Sign In
            </button>
            {showModal && (
                <div className="modal-wrapper">
                    <div className="modal" onClick={handleToggleModal}>
                        <div className="modal-content" onClick={(e) => e.stopPropagation()}>
                            <span className="close" onClick={handleToggleModal}>&times;</span>
                            <h2>{isSignIn ? "Sign In" : "Sign Up"}</h2>
                            <form className="auth-form">
                                <input type="text" placeholder="Username" />
                                <input type="password" placeholder="Password" />
                                {!isSignIn && <input type="password" placeholder="Confirm Password" />}
                                <button type="submit">{isSignIn ? "Sign In" : "Sign Up"}</button>
                            </form>
                            <p onClick={handleToggleForm}>
                                {isSignIn ? "New to My Journal? Sign Up" : "Already have an account? Sign In"}
                            </p>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
}

export default Navbar;
