import React, { useState, useEffect } from "react";
import axios from "axios"; // Import Axios library
import "../Styles/Navbar.css";

function Navbar() {
    const [showModal, setShowModal] = useState(false);
    const [isSignIn, setIsSignIn] = useState(true);
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");
    const [loggedInUser, setLoggedInUser] = useState(null);

    useEffect(() => {
        // Check if user is already logged in
        // You might need to implement a way to persist authentication state, like using tokens or sessions
        // For this example, we'll assume a token is stored in localStorage upon successful login
        const token = localStorage.getItem("token");
        if (token) {
            // Fetch user data or set username from token
            setLoggedInUser("user"); // Replace "user" with actual username or user object
        }
    }, []);

    const handleToggleModal = () => {
        setShowModal(!showModal);
    };

    const handleToggleForm = () => {
        setIsSignIn(!isSignIn);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (isSignIn) {
            try {
                const response = await axios.post("/api/login", { username, password });
                localStorage.setItem("token", response.data.token); // Save token to localStorage
                setLoggedInUser(username); // Set logged-in user
            } catch (error) {
                console.error("Login failed", error.response.data);
            }
        } else {
            try {
                const response = await axios.post("/api/register", { username, password });
                localStorage.setItem("token", response.data.token); // Save token to localStorage
                setLoggedInUser(username); // Set logged-in user
            } catch (error) {
                console.error("Registration failed", error.response.data);
            }
        }
    };

    const handleLogout = () => {
        localStorage.removeItem("token"); // Remove token from localStorage
        setLoggedInUser(null); // Clear logged-in user
    };

    return (
        <div className="navbar">
            <h1>My Journal</h1>
            {loggedInUser ? (
                <div>
                    <span>Welcome, {loggedInUser}</span>
                    <button className="logout-btn" onClick={handleLogout}>Logout</button>
                </div>
            ) : (
                <button className="sign-in-btn" onClick={handleToggleModal}>
                    Sign In
                </button>
            )}
            {showModal && (
                <div className="modal-wrapper">
                    <div className="modal" onClick={handleToggleModal}>
                        <div className="modal-content" onClick={(e) => e.stopPropagation()}>
                            <span className="close" onClick={handleToggleModal}>&times;</span>
                            <h2>{isSignIn ? "Sign In" : "Sign Up"}</h2>
                            <form className="auth-form" onSubmit={handleSubmit}>
                                <input type="text" placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)} />
                                <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
                                {!isSignIn && <input type="password" placeholder="Confirm Password" value={confirmPassword} onChange={(e) => setConfirmPassword(e.target.value)} />}
                                <button type="submit">{isSignIn ? "Sign In" : "Sign Up"}</button>
                            </form>
                            <p onClick={handleToggleForm}>
                                {isSignIn ? "New to My Journal? Sign Up" : "Already have an account? Sign In"}
                            </p>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
}

export default Navbar;
